package com.c.conditional.practise;

public class AIfStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age=20;
		if(age>18)
		{
			System.out.println("Age is greater than 18. : "+age);
		}
		
		System.out.println("If statement example.");
		
		if(age>33)
		{
			System.out.println("Age is greater than 33. : "+age);
		}
		
		System.out.println("If statement example1.");


	}

}
