package com.c.conditional.practise;

public class GForEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int arr[]= {1,2,3,4,5,6};
		for(int i:arr)
		{
			System.out.println("Bishnu: "+i);
		}
		
		System.out.println("................");
		
		char arr1[]= {'A','B','C','D','E','F'};
		for(char i:arr1)
		{
			System.out.println("Bishnu: "+i);
		}

	}

}
